import React, { useState } from 'react';
import { Box, Card, Typography, CardActions, CardContent, Button, Modal } from '@mui/material';

const bull = (
  <Box
    component="span"
    sx={{ display: 'inline-block', mx: '2px', transform: 'scale(0.8)' }}
  >
    â€¢
  </Box>
);

const Store = () => {
  const [open, setOpen] = useState(false);

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  return (
    <Box sx={{ width: '100%', margin: '20px', display: 'flex', justifyContent: 'center' }}>
      <Box sx={{ width: '80%' }}>
        <Typography variant="h4" component="div" gutterBottom>
          Store
        </Typography>
        <Box sx={{ display: 'flex', flexDirection: 'row', gap: '2em' }}>
          {[...Array(3)].map((_, index) => (
            <Card variant="outlined" key={index} sx={{ width: '25%' }}>
              <CardContent>
                <Typography gutterBottom sx={{ color: 'text.secondary', fontSize: 14 }}>
                  Essentials
                </Typography>
                <Typography variant="h5" component="div">
                  Sofa
                </Typography>
                <Typography variant="body2" component="div">
                  <img src="https://placehold.co/600x400" alt="Sofa" style={{ width: '100%', marginTop: '1em', marginBottom: '1em' }} />
                </Typography>
                <Typography variant="body2">
                  A cozy, three-seater sofa with soft cushions, a sturdy frame, and plush fabric upholstery, perfect for relaxing or entertaining in any living space.
                </Typography>
              </CardContent>
              <CardActions>
                <Button size="large" onClick={handleOpen}>Buy now</Button>
              </CardActions>
            </Card>
          ))}
        </Box>

        {/* Modal */}
        <Modal
          open={open}
          onClose={handleClose}
          aria-labelledby="modal-modal-title"
          aria-describedby="modal-modal-description"
        >
          <Box
            sx={{
              position: 'absolute',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              width: 400,
              bgcolor: 'background.paper',
              border: '2px solid #000',
              boxShadow: 24,
              p: 4,
            }}
          >
            <Typography id="modal-modal-title" variant="h6" component="h2" style={{ color: 'black' }}>
              Confirm Payment
            </Typography>
            <Typography id="modal-modal-description" sx={{ mt: 2 }} style={{ color: 'black', textAlign: 'center' }}>
              <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 2 }}>
                <Button variant='outlined' style={{ width: '90%'}}>Paypal</Button>
                <Button variant='outlined' style={{ width: '90%'}}>Credit Card</Button>
                <Button variant='outlined' style={{ width: '90%'}}>SPay</Button>
              </Box>
            </Typography>
          </Box>
        </Modal>
      </Box>
    </Box>
  );
};

export default Store;
